import { createSlice } from '@reduxjs/toolkit'


const initialState = {
    itemCount: 0,
    totalAmount : 0
}

const CartSlice = createSlice({
    name : 'cart',
    initialState,
    reducers : {
        incrementItemCount : (state) => {
            state.itemCount += 1;
        },
        decrementItemCount : (state) => {
            state.itemCount -= 1;
        },
        incrementInAmount : (state,amount) => {
            state.totalAmount += amount;
        },
        decrementInAmount : (state,amount) => {
            state.totalAmount -= amount;
        }
    }
});

export const {incrementItemCount,decrementItemCount,incrementInAmount,decrementInAmount} = CartSlice.actions;
export default CartSlice.reducer;
