import { createSlice } from '@reduxjs/toolkit'


const initialState = {
    itemCount: 0,
    totalAmount : 0
}

const CartSlice = createSlice({
    name : 'cart',
    initialState,
    reducers : {
        AddToCart : (state,amount) => {
            state.itemCount += 1;
            state.totalAmount += amount;
        },
        removeFromCartCount : (state,amount) => {
            state.itemCount -= 1;
            state.totalAmount -= amount;
        }
    }
});

export const {AddToCart,removeFromCartCount} = CartSlice.actions;
export default CartSlice.reducer;
