import React from 'react'
import { useSelector } from 'react-redux';

import { Link } from 'react-router-dom';
import CartItem from '../Components/CartItem';

const CartPage = () => {
  const totalItem = useSelector((state) => state.cart.itemCount );
  const totalAmount = useSelector((state) => state.cart.totalAmount );
  const cartItems = useSelector((state)=> state.cart.inCart); 
  console.log(cartItems)
  return (
    <div className='w-11/12 max-w-[1000px] m-auto my-10'>
      {
        totalItem > 0 ? 
        (
          <div className='flex flex-row justify-between'>
            <div className='w-[50%] flex flex-col'>
              {
                cartItems.map((item,index) => (
                  <CartItem key={index} item={item}/>
                ))
              } 
            </div>
            <div className='mt-[20px] max-h-screen min-h-fit'>
              <p className='text-xl text-green-600'>Your Cart</p>
              <p className='uppercase text-3xl font-bold text-green-700'>summary</p>
              <p className='font-semibold '>Total Items: <span> {totalItem}</span> </p>
              <p>Total Amount:<span>{totalAmount}</span> </p>
              <button>CheckOutNow</button>
            </div>
          </div>
        ):
        (
          <div>
            <div>Empty Cart</div>
            <Link to='/'>Shop Now</Link>
          </div>
        )
      }
    </div>
  )
}

export default CartPage;
