import { createSlice } from '@reduxjs/toolkit'
// import { useState } from 'react';

// const [inCart,setInCart] = useState([]);

const initialState = {
    itemCount: 0,
    totalAmount : 0,
    inCart : []
    // setInCart
}

const CartSlice = createSlice({
    name : 'cart',
    initialState,
    reducers : {
        AddToCart : async (state,item) => {
            state.itemCount += 1;
            state.totalAmount += item.price;
            state.inCart.push(item);
            // let items = state.inCart;
            // items.push(item);
            // state.setInCart(items);
        },
        removeFromCartCount : async (state,item) => {
            state.itemCount -= 1;
            state.totalAmount -= item.price;
            // let items = state.inCart;
            state.inCart = state.inCart.filter(listIn => listIn !== item );
            // state.setInCart(items);
        }
    }
});

export const {AddToCart,removeFromCartCount} = CartSlice.actions;
export default CartSlice.reducer;
