import React from 'react'
import { useSelector } from 'react-redux';

import { Link } from 'react-router-dom';
import CartItem from '../Components/CartItem';

const CartPage = () => {
  const totalItem = useSelector((state) => state.cart.itemCount );
  const totalAmount = useSelector((state) => state.cart.totalAmount );
  const cartItems = useSelector((state)=> state.cart.inCart);
  return (
    <div>
      {
        totalItem > 0 ? 
        (
          <div>
            <div>
            {
              cartItems.map((item,index) => {<CartItem key={index} item={item}/>})
            }
            </div>
            <div>
              <p>Your Cart</p>
              <p className='uppercase'>summary</p>
              <p>Total Items:<span>{totalItem}</span> </p>
              <p>Total Amount:<span>{totalAmount}</span> </p>
              <button>CheckOutNow</button>
            </div>
          </div>
        ):
        (
          <div>
            <div>Empty Cart</div>
            <Link to='/'>Shop Now</Link>
          </div>
        )
      }
    </div>
  )
}

export default CartPage;
